from rest_framework.serializers import ModelSerializerfrom rest_framework.viewsets import ModelViewSetfrom rest_framework import permissionsfrom core.models.category import Categoryfrom utils.custom_pagination import CustomPaginationclass CategorySerializer(ModelSerializer):    class Meta:        model = Category        fields = '__all__'class CategoryViewSet(ModelViewSet):    queryset = Category.objects.all()    serializer_class = CategorySerializer    permission_classes = [permissions.AllowAny, ]    pagination_class = CustomPagination