from rest_framework.serializers import ModelSerializerfrom rest_framework.viewsets import ModelViewSetfrom rest_framework.permissions import IsAdminUserfrom core.models.location import Locationfrom utils.custom_pagination import CustomPaginationclass LocationSerializer(ModelSerializer):    class Meta:        model = Location        fields = '__all__'class LocationViewSet(ModelViewSet):    queryset = Location.objects.all()    serializer_class = LocationSerializer    permission_classes = [IsAdminUser, ]    pagination_class = CustomPagination