from rest_framework.serializers import ModelSerializerfrom rest_framework.viewsets import ModelViewSetfrom rest_framework.permissions import IsAuthenticatedfrom service.models.order import Orderclass OrderSerializer(ModelSerializer):    class Meta:        model = Order        fields = '__all__'        read_only_fields = ['user']class OrderViewSet(ModelViewSet):    queryset = Order.objects.all()    serializer_class = OrderSerializer    permission_classes = [IsAuthenticated, ]    def perform_create(self, serializer):        serializer.save(user=self.request.user)    def get_queryset(self):        if not self.request.user.is_superuser:            order = Order.objects.filter(user=self.request.user)        else:            order = Order.objects.all()        return order