from rest_framework.serializers import ModelSerializerfrom rest_framework.viewsets import ModelViewSetfrom rest_framework.permissions import IsAdminUserfrom service.models.review import Reviewclass ReviewSerializer(ModelSerializer):    class Meta:        model = Review        fields = '__all'        read_only_fields = ['owner']class ReviewViewSet(ModelViewSet):    queryset = Review.objects.all()    serializer_class = ReviewSerializer    permission_classes = [IsAdminUser]