from rest_framework.serializers import ModelSerializerfrom rest_framework.viewsets import ModelViewSetfrom rest_framework.permissions import IsAdminUserfrom service.models.review import Reviewclass ReviewSerializer(ModelSerializer):    class Meta:        model = Review        fields = '__all__'        read_only_fields = ['user', 'approved_review']class ReviewViewSet(ModelViewSet):    """    API endpoint that allows reviews to be viewed or    """    queryset = Review.objects.all()    serializer_class = ReviewSerializer    permission_classes = [IsAdminUser]    def perform_create(self, serializer):        serializer.save(user=self.request.user)