from rest_framework.serializers import ModelSerializerfrom rest_framework.viewsets import ModelViewSetfrom rest_framework.permissions import IsAdminUserfrom service.models.order import Scheduleclass ScheduleSerializer(ModelSerializer):    class Meta:        model = Schedule        fields = '__all__'class ScheduleViewSet(ModelViewSet):    queryset = Schedule.objects.all()    serializer_class = ScheduleSerializer    permission_classes = [IsAdminUser, ]