from rest_framework.serializers import ModelSerializerfrom rest_framework.viewsets import ModelViewSetfrom rest_framework.permissions import IsAuthenticatedfrom service.models.service import Servicefrom api.permissions import PermissionHelperMixinclass ServiceSerializer(ModelSerializer):    class Meta:        model = Service        fields = '__all__'class ServiceViewSet(ModelViewSet, PermissionHelperMixin):    queryset = Service.objects.all()    serializer_class = ServiceSerializer    def get_permissions(self):        return self.admin_editable_only()