from django.forms import ModelForm, TextInput, Select, FileInput, NumberInputfrom core.models.category import Categoryclass CategoryForm(ModelForm):    class Meta:        model = Category        fields = '__all__'        widgets = {            'name': TextInput(attrs={'class': 'form-control', 'id': 'name'}),            'parent': Select(attrs={'class': 'form-control'}),            'category_image': FileInput(attrs={'class': 'custom-file-input', 'id': 'imageUpload'}),            'order_by': NumberInput(attrs={'class': 'form-control'})        }