from django import formsfrom core.models.category import Categoryclass CategoryForm(forms.ModelForm):    class Meta:        model = Category        fields = '__all__'        widgets = {            'name': forms.TextInput(attrs={'class': 'form-control', 'id': 'name'}),            'parent': forms.Select(attrs={'class': 'form-control'}),            'category_image': forms.FileInput(attrs={'class': 'custom-file-input', 'id': 'imageUpload'}),            'order_by': forms.NumberInput(attrs={'class': 'form-control'})        }