from django import formsfrom core.models.location import Locationclass LocationForm(forms.ModelForm):    class Meta:        model = Location        fields = '__all__'        widgets = {            'name': forms.TextInput(attrs={'class': 'form-control', 'id': 'name'}),            'parent': forms.Select(attrs={'class': 'form-control'}),            'location_type': forms.Select(attrs={'class': 'form-control'}),            'is_active': forms.CheckboxInput(attrs={'class': 'checkbox', 'id': 'location_activity'})        }