from django.db import modelsfrom .base import BaseEntityclass Category(BaseEntity):    name = models.CharField(max_length=70)    parent = models.ForeignKey('self', on_delete=models.CASCADE, null=True, blank=True, related_name='parent_category')    category_image = models.ImageField(upload_to='category/%y/%m/%d', blank=True)    order_by = models.BigIntegerField(default=0)    def __str__(self):        return self.name    class Meta:        ordering = ('-id',)    def get_children(self):        return Category.objects.filter(parent=self)    def children_count(self):        return Category.objects.filter(parent=self).count()