from django.db import modelsfrom .base import BaseEntityclass Location(BaseEntity):    name = models.CharField(max_length=95)    parent = models.ForeignKey('self', on_delete=models.CASCADE, blank=True, null=True, default=None)    is_active = models.BooleanField(default=False)    class Meta:        ordering = ['-id']    def __str__(self):        return self.name    def get_children(self):        return Location.objects.filter(parent=self)    def children_count(self):        return Location.objects.filter(parent=self).count()