from django.db import modelsfrom .base import BaseEntityfrom core.utlity import LOCATION_TYPE_CHOICESclass Location(BaseEntity):    name = models.CharField(max_length=95)    location_type = models.IntegerField(choices=LOCATION_TYPE_CHOICES, blank=True)    parent = models.ForeignKey('self', on_delete=models.CASCADE, blank=True, null=True, default=None)    is_active = models.BooleanField(default=False)    class Meta:        ordering = ['-id']    def __str__(self):        return self.name    @staticmethod    def get_division_queryset():        return Location.objects.filter(location_type=7)    @staticmethod    def get_city_queryset():        return Location.objects.filter(location_type=2)    @staticmethod    def get_thana_queryset():        return Location.objects.filter(location_type=4)    @staticmethod    def get_postcode_queryset():        return Location.objects.filter(location_type=5)    @staticmethod    def get_area_queryset():        return Location.objects.filter(location_type=3)    def to_string(self):        locations = []        starts = self        while starts:            locations.append(starts.name)            try:                starts = starts.parent            except Exception as e:                starts = None        return ', '.join(locations)