from django.urls import pathfrom core.views.auth import Login, Logout, SingUpViewfrom core.views.dashboard import Dashboardfrom core.views.category_view import CategoryCreateListView, CategoryUpdateView, CategoryDeleteViewfrom core.views.location_view import LocationCreateListView, LocationDeleteViewfrom core.views.homepage_view import Homepageurlpatterns = [    path('', Homepage.as_view(), name='homepage'),    path('dashboard/', Dashboard.as_view(), name='dashboard'),    path('login/', Login.as_view(), name='login'),    path('logout/', Logout.as_view(), name='logout'),    path('signup/', SingUpView.as_view(), name='signup'),    path('category/', CategoryCreateListView.as_view(), name='category'),    path('category/<int:id>/', CategoryUpdateView.as_view(), name='category_update'),    path('category/delete/<int:id>/', CategoryDeleteView.as_view(), name='category_delete'),    path('location/', LocationCreateListView.as_view(), name='location'),    path('location/<int:id>/', LocationDeleteView.as_view(), name='location_delete'),]