from django.urls import pathfrom core.views import auth_viewfrom core.views import dashboardfrom core.views import category_viewfrom core.views import location_viewfrom core.views import homepage_viewurlpatterns = [    path('', homepage_view.Homepage.as_view(), name='homepage'),    path('dashboard/', dashboard.Dashboard.as_view(), name='dashboard'),    # Auth    path('login/', auth_view.Login.as_view(), name='login'),    path('logout/', auth_view.Logout.as_view(), name='logout'),    path('signup/', auth_view.SingUpView.as_view(), name='signup'),    path('forgot-password/', auth_view.ForgotPasswordView.as_view(), name='forgot_password'),    # Categories    path('category/', category_view.CategoryCreateListView.as_view(), name='category'),    path('category/<pk>/', category_view.CategoryUpdate.as_view(), name='category_update'),    path('category/delete/<pk>/', category_view.CategoryDelete.as_view(), name='category_delete'),    # Location    path('location/', location_view.LocationCreateListView.as_view(), name='location'),    path('location/<pk>/', location_view.LocationDeleteView.as_view(), name='location_delete'),    path('location-filter/', location_view.LocationFilterView.as_view(), name='location_filter'),]