from django.views.generic.edit import CreateViewfrom django.contrib.auth.decorators import login_requiredfrom django.utils.decorators import method_decoratorfrom core.forms.category_form import CategoryFormfrom core.models.category import Category@method_decorator(login_required(login_url='/login/'), name='dispatch')class CreateCategory(CreateView):    template_name = 'category/category.html'    model = Category    form_class = CategoryForm    success_url = '/add-category'    def get_context_data(self, **kwargs):        return dict(            super(CreateCategory, self).get_context_data(**kwargs),            category=Category.objects.all()        )