from django.views.generic.base import TemplateViewfrom django.contrib.auth.decorators import login_requiredfrom django.utils.decorators import method_decoratorfrom service.models.order import Service, Orderfrom core.models.category import Categoryfrom user.models import Userclass Dashboard(TemplateView):    template_name = 'dashboard/dashboard.html'    @method_decorator(login_required)    def dispatch(self, *args, **kwargs):        return super(Dashboard, self).dispatch(*args, **kwargs)    def get_context_data(self, *args, **kwargs):        context = super(Dashboard, self).get_context_data(**kwargs)        context['services'] = Service.objects.all()        context['orders'] = Order.objects.all().order_by('-id')        context['category'] = Category.objects.all()        context['users'] = User.objects.all()        return context