from django.views.generic import CreateView, ListViewfrom django.contrib.auth.decorators import login_requiredfrom django.utils.decorators import method_decoratorfrom django.core.paginator import Paginator, EmptyPage, PageNotAnIntegerfrom django.contrib.messages.views import SuccessMessageMixinfrom django.shortcuts import redirectfrom core.models.location import Locationfrom core.forms.location_form import LocationForm@method_decorator(login_required(login_url='/login/'), name='dispatch')class LocationCreateListView(SuccessMessageMixin, CreateView, ListView):    template_name = 'location/location.html'    model = Location    form_class = LocationForm    paginate_by = 6    context_object_name = 'location'    def get_context_data(self, **kwargs):        return dict(            super(LocationCreateListView, self).get_context_data(**kwargs),            category=self.model.objects.all().order_by('-id')        )