from django.views.generic import CreateView, ListView, DeleteViewfrom django.contrib.auth.decorators import login_requiredfrom django.utils.decorators import method_decoratorfrom django.contrib.messages.views import SuccessMessageMixinfrom django_filters.views import FilterViewfrom core.models.location import Locationfrom core.forms.location_form import LocationFormfrom core.locaiton_filter import LocationFilter@method_decorator(login_required(login_url='/login/'), name='dispatch')class LocationCreateListView(SuccessMessageMixin, CreateView, ListView):    template_name = 'location/location.html'    model = Location    form_class = LocationForm    paginate_by = 6    context_object_name = 'location'    success_url = '/location/'    success_message = "Location successfully created!"    def get_context_data(self, **kwargs):        return dict(            super(LocationCreateListView, self).get_context_data(**kwargs),            category=self.model.objects.all().order_by('-id')        )@method_decorator(login_required(login_url='/login/'), name='dispatch')class LocationDeleteView(DeleteView):    template_name = 'location/location_confirm_delete.html'    model = Location    success_url = '/location/'@method_decorator(login_required(login_url='/login/'), name='dispatch')class LocationFilterView(FilterView, ListView):    template_name = 'location/location_filter.html'    model = Location    filter_class = LocationFilter    context_object_name = 'location'