from django.forms import ModelFormfrom django.forms.widgets import Select, NumberInput, DateInputfrom service.models.order import Orderclass OrderForm(ModelForm):    class Meta:        model = Order        fields = '__all__'        exclude = ('user',)        widgets = {            'item': Select(attrs={'class': 'form-control', 'id': 'item'}),            'schedule': Select(attrs={'class': 'form-control', 'id': 'schedule'}),            'order_date': DateInput(attrs={'class': 'form-control', 'type': 'date', 'id': 'order_date'}),            'phone_number': NumberInput(attrs={'class': 'form-control', 'id': 'phone_number'})        }