from django.forms import ModelFormfrom django.forms.widgets import Select, NumberInput, DateInput, TextInput, CheckboxInputfrom service.models.order import Orderclass OrderForm(ModelForm):    class Meta:        model = Order        fields = '__all__'        exclude = ('user',)        widgets = {            'item': Select(attrs={'class': 'form-control', 'id': 'item'}),            'schedule': Select(attrs={'class': 'form-control', 'id': 'schedule'}),            'location': Select(attrs={'class': 'form-control', 'id': 'location'}),            'status': Select(attrs={'class': 'form-control', 'id': 'status'}),            'payment_status': CheckboxInput(attrs={'class': 'checkbox-inline', 'id': 'status'}),            'order_date': DateInput(format='%d-%m-%Y',                                    attrs={'class': 'form-control', 'type': 'date', 'id': 'order_date'}),            'phone_number': NumberInput(attrs={'class': 'form-control', 'id': 'phone_number'}),            'address': TextInput(attrs={'class': 'form-control', 'id': 'phone_number', 'placeholder': 'House: 30, '                                                                                                      'Road: 10/B, '                                                                                                      'Sector#12'})        }class OrderUpdateForm(ModelForm):    class Meta:        model = Order        fields = '__all__'        exclude = ('user',)        widgets = {            'item': Select(attrs={'class': 'form-control', 'id': 'item', 'disabled': 'disabled'}),            'schedule': Select(attrs={'class': 'form-control', 'id': 'schedule', 'disabled': 'disabled'}),            'location': Select(attrs={'class': 'form-control', 'id': 'location', 'disabled': 'disabled'}),            'status': Select(attrs={'class': 'form-control', 'id': 'status'}),            'payment_status': CheckboxInput(attrs={'class': 'checkbox-inline', 'id': 'status', 'disabled': 'disabled'}),            'order_date': DateInput(format='%d-%m-%Y',                                    attrs={'class': 'form-control', 'type': 'date', 'id': 'order_date'}),            'phone_number': NumberInput(attrs={'class': 'form-control', 'id': 'phone_number', 'disabled': 'disabled'}),            'address': TextInput(attrs={'class': 'form-control', 'id': 'phone_number', 'placeholder': 'House: 30, '                                                                                                      'Road: 10/B, '                                                                                                      'Sector#12',                                        'disabled': 'disabled'})        }