from django import formsfrom service.models.order import Orderclass OrderForm(forms.ModelForm):    class Meta:        model = Order        fields = '__all__'        exclude = ('user',)        widgets = {            'item': forms.Select(attrs={'class': 'form-control', 'id': 'item'}),            'schedule': forms.Select(attrs={'class': 'form-control', 'id': 'schedule'}),            'location': forms.Select(attrs={'class': 'form-control', 'id': 'location'}),            'status': forms.Select(attrs={'class': 'form-control', 'id': 'status'}),            'payment_status': forms.CheckboxInput(attrs={'class': 'checkbox-inline', 'id': 'status'}),            'order_date': forms.DateInput(format='%d-%m-%Y',                                          attrs={'class': 'form-control', 'type': 'date', 'id': 'order_date'}),            'phone_number': forms.NumberInput(attrs={'class': 'form-control', 'id': 'phone_number'}),            'address': forms.TextInput(                attrs={'class': 'form-control', 'id': 'phone_number', 'placeholder': 'House: 30, '                                                                                     'Road: 10/B, '                                                                                     'Sector#12'})        }