from django import formsfrom service.models.review import Reviewclass ReviewForm(forms.ModelForm):    class Meta:        model = Review        exclude = (            'user', 'approved_review'        )        fields = '__all__'        widgets = {            'title': forms.TextInput(                attrs={'class': 'form-control', 'id': 'title', 'placeholder': 'Enter service title'}),            'user': forms.Select(attrs={'class': 'form-control', 'id': 'user'}),            'service': forms.Select(attrs={'class': 'form-control', 'id': 'service'}),            'rating': forms.NumberInput(attrs={'class': 'form-control', 'id': 'rating'}),            'review': forms.TextInput(                attrs={'class': 'form-control', 'id': 'review_text', 'placeholder': 'Enter review details'})        }