from django.forms import ModelFormfrom django.forms.widgets import TextInput, Select, NumberInputfrom service.models.review import Reviewclass ReviewForm(ModelForm):    class Meta:        model = Review        fields = '__all__'        widgets = {            'title': TextInput(                attrs={'class': 'form-control', 'id': 'title', 'placeholder': 'Enter service tilte'}),            'user': Select(attrs={'class': 'form-control', 'id': 'user'}),            'review': Select(attrs={'class': 'form-control', 'id': 'review'}),            'rating': NumberInput(attrs={'class': 'form-control', 'id': 'rating'}),            'review_text': TextInput(attrs={'class': 'form-control', 'id': 'review_text', 'placeholder': 'Review '                                                                                                         'description'})        }