from django.forms import ModelFormfrom django.forms.widgets import TextInput, Select, NumberInputfrom service.models.review import Reviewclass ReviewForm(ModelForm):    class Meta:        model = Review        exclude = (            'user', 'approved_review'        )        fields = '__all__'        widgets = {            'title': TextInput(                attrs={'class': 'form-control', 'id': 'title', 'placeholder': 'Enter service title'}),            'user': Select(attrs={'class': 'form-control', 'id': 'user'}),            'service': Select(attrs={'class': 'form-control', 'id': 'service'}),            'rating': NumberInput(attrs={'class': 'form-control', 'id': 'rating'}),            'review': TextInput(                attrs={'class': 'form-control', 'id': 'review_text', 'placeholder': 'Enter review details'})        }