from django.forms import ModelForm, TextInput, CheckboxInput, TimeInputfrom service.models.order import Scheduleclass ScheduleForm(ModelForm):    class Meta:        model = Schedule        fields = '__all__'        widgets = {            'name': TextInput(attrs={'class': 'form-control', 'id': 'name', 'placeholder': 'Enter schedule name'}),            'is_active': CheckboxInput(attrs={'class': 'checkbox', 'id': 'is_active'}),            'time': TimeInput(attrs={'class': 'form-control', 'id': 'time', 'type': 'time'}),        }