from django.forms import ModelFormfrom django.forms.widgets import TextInput, DateInput, TimeInputfrom service.models.order import Scheduleclass ScheduleForm(ModelForm):    class Meta:        model = Schedule        fields = '__all__'        widgets = {            'name': TextInput(attrs={'class': 'form-control', 'id': 'name'}),            'date': DateInput(attrs={'class': 'form-control', 'id': 'date', 'type': 'date'}),            'time': TimeInput(attrs={'class': 'form-control', 'id': 'time', 'type': 'time'}),        }