from django import formsfrom service.models.order import Scheduleclass ScheduleForm(forms.ModelForm):    class Meta:        model = Schedule        fields = '__all__'        widgets = {            'name': forms.TextInput(                attrs={'class': 'form-control', 'id': 'name', 'placeholder': 'Enter schedule name'}),            'is_active': forms.CheckboxInput(attrs={'class': 'checkbox', 'id': 'is_active'}),            'time': forms.TimeInput(attrs={'class': 'form-control', 'id': 'time', 'type': 'time'}),        }