from django import formsfrom service.models.service import Serviceclass ServiceForm(forms.ModelForm):    class Meta:        model = Service        fields = '__all__'        exclude = ('owner',)        widgets = {            'category': forms.Select(attrs={'class': 'form-control', 'id': 'category'}),            'location': forms.Select(attrs={'class': 'form-control', 'id': 'location'}),            'is_active': forms.CheckboxInput(attrs={'class': 'custom-control-label ml-4', 'id': 'sdf'}),            'service_image': forms.FileInput(attrs={'class': 'custom-file-input', 'id': 'imageUpload'}),            'service_name': forms.TextInput(                attrs={'class': 'form-control', 'id': 'service_name', 'placeholder': 'Enter service name'}),            'package_name': forms.TextInput(                attrs={'class': 'form-control', 'id': 'package_name', 'placeholder': 'Enter package name'}),            'price': forms.NumberInput(                attrs={'class': 'form-control', 'id': 'price', 'placeholder': 'Enter service price'}),            'discount_price': forms.NumberInput(attrs={'class': 'form-control', 'id': 'price2'}),            'quick_delivery_charge': forms.NumberInput(attrs={'class': 'form-control', 'id': 'price3'})        }