from django.forms import ModelFormfrom django.forms.widgets import TextInput, Select, CheckboxInput, FileInput, NumberInputfrom service.models.service import Serviceclass ServiceForm(ModelForm):    class Meta:        model = Service        fields = '__all__'        exclude = ['owner']        widgets = {            'service_name': TextInput(                attrs={'class': 'form-control', 'id': 'service_name', 'placeholder': 'Enter service name'}),            'package_name': TextInput(                attrs={'class': 'form-control', 'id': 'package_name', 'placeholder': 'Enter package name'}),            'category': Select(attrs={'class': 'form-control', 'id': 'category'}),            'location': Select(attrs={'class': 'form-control', 'id': 'location'}),            'price': NumberInput(attrs={'class': 'form-control', 'id': 'price', 'placeholder': 'Enter service price'}),            'discount_price': NumberInput(attrs={'class': 'form-control', 'id': 'price2', 'placeholder':                'Dicount service price'}),            'quick_delivery_charge': NumberInput(attrs={'class': 'form-control', 'id': 'price3', 'placeholder': 'Quick '                                                                                                                'delivery charge'                                                                                                                ''}),            'is_active': CheckboxInput(attrs={'class': 'custom-control-label ml-4', 'id': 'sdf'}),            'service_image': FileInput(attrs={'class': 'custom-file-input', 'id': 'imageUpload'}),        }