from django.db import modelsfrom django.contrib.auth.models import Userfrom core.models.base import BaseEntityfrom core.models.category import Categoryfrom core.models.location import Locationclass Service(BaseEntity):    owner = models.ForeignKey(User, on_delete=models.CASCADE, null=True, related_name='service_owner')    service_name = models.CharField(max_length=60)    category = models.ForeignKey(Category, on_delete=models.CASCADE, null=True, related_name='service_category')    location = models.ForeignKey(Location, on_delete=models.CASCADE, null=True, related_name='service_location')    price = models.IntegerField(default=0)    discount_price = models.IntegerField(default=0)    quick_delivery_charge = models.IntegerField(default=0)    is_active = models.BooleanField(default=True)    package_name = models.CharField(max_length=50, blank=True, null=True)    service_image = models.ImageField(upload_to='service', blank=True, null=True)    def __str__(self):        return self.service_name