from django.db import modelsfrom core.models.base import BaseEntityfrom core.models.category import Categoryfrom core.models.location import Locationfrom user.models import Agentclass Service(BaseEntity):    owner = models.ForeignKey(Agent, on_delete=models.CASCADE, null=True, related_name='service_owner')    service_name = models.CharField(max_length=60)    category = models.ForeignKey(Category, on_delete=models.CASCADE, null=True, related_name='service_category')    location = models.ForeignKey(Location, on_delete=models.CASCADE, null=True, related_name='service_location')    price = models.IntegerField(default=0)    discount_price = models.IntegerField(default=0)    quick_delivery_charge = models.IntegerField(default=0)    is_active = models.BooleanField(default=True)    package_name = models.CharField(max_length=50, blank=True, null=True)    service_image = models.ImageField(upload_to='service', blank=True, null=True)    def __str__(self):        return self.service_name    @property    def category_name(self):        return self.category.name    @property    def location_name(self):        return self.location.name