from django.urls import pathfrom service.views.service_view import AddService, ServiceList, ServiceDetail, ServiceDeleteView, ServiceUpdatefrom service.views.order_view import OrderList, CreateOrder, OrderDetailView, OrderUpdateView, OrderRequestListfrom service.views.schedule_view import ScheduleCreateListView, ScheduleDeleteView, ScheduleUpdateViewurlpatterns = [    path('add-service/', AddService.as_view(), name='add_service'),    path('service-list/', ServiceList.as_view(), name='service_list'),    path('service-detail/<pk>/', ServiceDetail.as_view(), name='service_detail'),    path('service-update/<pk>/', ServiceUpdate.as_view(), name='service_update'),    path('service-delete/<pk>/', ServiceDeleteView.as_view(), name='service_delete'),    path('new-order/', CreateOrder.as_view(), name='create_order'),    path('order-list/', OrderList.as_view(), name='order_list'),    path('order-detail/<pk>/', OrderDetailView.as_view(), name='order_detail'),    path('order-update/<pk>/', OrderUpdateView.as_view(), name='order_update'),    path('order/order-request/', OrderRequestList.as_view(), name='order_request'),    path('schedule/', ScheduleCreateListView.as_view(), name='schedule'),    path('schedule-delete/<pk>/', ScheduleDeleteView.as_view(), name='schedule_delete'),    path('schedule-update/<pk>/', ScheduleUpdateView.as_view(), name='schedule_update'),]