from django.views.generic import CreateView, ListView, UpdateView, DeleteViewfrom django.contrib.auth.decorators import login_requiredfrom django.utils.decorators import method_decoratorfrom django.contrib.messages.views import SuccessMessageMixinfrom service.models.order import Schedulefrom service.forms.schedule_form import ScheduleForm@method_decorator(login_required(login_url='/login/'), name='dispatch')class ScheduleCreateListView(SuccessMessageMixin, CreateView, ListView):    template_name = 'schedule/schedule_list.html'    model = Schedule    form_class = ScheduleForm    success_url = '/service/schedule/'    success_message = 'Schedule has been created successfully!'    paginate_by = 7    context_object_name = 'schedule'    def get_context_data(self, **kwargs):        return dict(            super(ScheduleCreateListView, self).get_context_data(**kwargs),            category=self.model.objects.all().order_by('-id')        )@method_decorator(login_required(login_url='/login/'), name='dispatch')class ScheduleUpdateView(UpdateView):    template_name = 'schedule/schedule_list.html'    model = Schedule    form_class = ScheduleForm@method_decorator(login_required(login_url='/login/'), name='dispatch')class ScheduleDeleteView(DeleteView):    template_name = 'schedule/schedule_confirm_delete.html'    model = Schedule    success_url = '/service/schedule/'