from django.views.generic import CreateView, ListViewfrom django.contrib.auth.decorators import login_requiredfrom django.utils.decorators import method_decoratorfrom django.contrib.messages.views import SuccessMessageMixinfrom service.models.order import Schedulefrom service.forms.schedule_form import ScheduleForm@method_decorator(login_required(login_url='/login/'), name='dispatch')class ScheduleCreateListView(SuccessMessageMixin, CreateView, ListView):    template_name = 'schedule/schedule_list.html'    model = Schedule    form_class = ScheduleForm    success_url = '/'    success_message = 'Schedule has been created successfully!'    paginate_by = 7    context_object_name = 'schedule'