from django.views.generic.base import TemplateViewfrom django.contrib.auth.decorators import login_requiredfrom django.utils.decorators import method_decoratorclass AddService(TemplateView):    template_name = 'services/add_service.html'    @method_decorator(login_required)    def dispatch(self, *args, **kwargs):        return super(AddService, self).dispatch(*args, **kwargs)class ServiceList(TemplateView):    template_name = 'services/service_list.html'    @method_decorator(login_required)    def dispatch(self, *args, **kwargs):        return super(ServiceList, self).dispatch(*args, **kwargs)