from django.views.generic.base import TemplateViewfrom django.views.generic import CreateViewfrom django.contrib.auth.decorators import login_requiredfrom django.utils.decorators import method_decoratorfrom django.contrib.messages.views import SuccessMessageMixinfrom service.models.service import Servicefrom service.forms.service_form import ServiceForm@method_decorator(login_required(login_url='/login/'), name='dispatch')class AddService(SuccessMessageMixin, CreateView):    template_name = 'services/add_service.html'    model = Service    form_class = ServiceForm    success_message = "Service has been successfully created!"class ServiceList(TemplateView):    template_name = 'services/service_list.html'    @method_decorator(login_required)    def dispatch(self, *args, **kwargs):        return super(ServiceList, self).dispatch(*args, **kwargs)