from django import formsfrom django.forms import TextInput, FileInput, NumberInput, Textarea, URLInputfrom .models import Agentclass CreateAgentFrom(forms.ModelForm):    class Meta:        model = Agent        fields = '__all__'        widgets = {            'company_name': TextInput(attrs={'class': 'form-control', 'id': 'company_name'}),            'nid_number': NumberInput(attrs={'class': 'form-control', 'id': 'nid_number'}),            'website': URLInput(attrs={'class': 'form-control', 'id': 'website'}),            'address': Textarea(attrs={'class': 'form-control', 'id': 'address'}),            'age': NumberInput(attrs={'class': 'form-control', 'id': 'age'}),            'contact_number': NumberInput(attrs={'class': 'form-control', 'id': 'contact_number'}),            'treat_lice': TextInput(attrs={'class': 'form-control', 'id': 'treat_lice'}),            'profile_picture': FileInput(attrs={'class': 'custom-file-input', 'id': 'imageUpload'}),        }